import * as types from "../types";

const initialState = {
  todos: [],
  todo: {},
  loading: false,
  error: null,
};

export const todoReducer = (state = initialState, action) => {
  switch (action.type) {
    case types.SET_TODOS:
      return {
        ...state,
        todos: action.payload,
        loading: false,
        error: null,
      };
    case types.POST_TODO:
      return {
        ...state,
        todos: [...state.todos, action.payload],
        loading: false,
        error: null,
      };
    case types.PUT_TODO:
      
      const _todos = [...state.todos];
      const index = _todos.findIndex(
        (element) => element.id == action.payload.id
      );

      if (index !== -1) {
        _todos[index] = action.payload;

        return {
          ...state,
          todos: [...state.todos, action.payload],
          loading: false,
          error: null,
        };
      }

    default:
      return state;
  }
};
