import Router from "next/router";
const Tasks = ({ tasks }) => {

  const handleClick = (event,id) =>{
    event.preventDefault(); 
    const element = document.getElementById(id)
    console.log(element)
    element.remove()
    

  }

  return (
    <>
      <ul>
        {tasks.map((task,i) => (
          <li key={i} id={task.id}>
            <button onClick={e => handleClick(e,task.id)}><i className="fas fa-trash-alt"></i></button>
            <label
              name={task.id}
              onClick={(e) => {
                e.preventDefault();
                Router.push("/tasks/[id]", `/tasks/${task.id}`);
              }}
            >
              {task.title}
            </label>
          </li>
        ))}
      </ul>
    </>
  );
};


export default Tasks;
