const getState = ({getStore,getActions, setStore})=>{
    return{
        store:{
            tasks:null
        },
        actions:{
            getTasks: url=>{
                return(
                    fetch(url,{
                        method:'GET',
                        headers:{
                            'Content-Type':'application/json'
                        }
                    })
                    .then(resp =>resp.json())
                    .then(data=>{
                        setStore({
                            tasks:data
                        });
                    })
                    .catch(error=> {
                        console.log(error)
                    })
                )
            }
        }
    }
}

export default getState;
