import { useRouter } from "next/router";
import Container from "../../components/Container";
import { useState } from "react";

const Task = ({ _task }) => {
  const router = useRouter();
  const { id } = router.query;
  const [visible, setVisible] = useState(false);

  // const task = event.target.task.value;
  // if (task && task.length != 0) {
  //   const object = {
  //     userId: 1,
  //     title: task,
  //     completed: false,
  //   };
  //   dispatch(postTodo(object));
  // }
  const handleClickUpdate = (event) => {
    event.preventDefault();
    setVisible(!visible)
  };
  const handleClickSave = (event) => {
    event.preventDefault();
    const update = document.getElementById("update");
    const cancel = document.getElementById("cancel");
    const textarea = document.getElementById("textarea");
    cancel.classList.add("d-none");
    update.classList.remove("d-none");
    textarea.classList.add("d-none");
    event.target.className += " d-none";
  };

  return (
    <Container>
      <div className="taskCard">
        <div className="card">
          <div className="card-body">
            <h5 className="card-title">Task: #{_task.id}</h5>
            <p className="card-text">
              Description: <br />
              {_task.title}
            </p>
          </div>
          <div className="card-footer">
            <form>
              <div className="d-flex mb-2">
                <button
                  id="update"
                  type="button"
                  className="btn btn-info"
                  onClick={(e) => handleClickUpdate(e)}
                >
                  Update
                </button>
                {visible && (
                  <div>
                    <button
                      id="save"
                      type="submit"
                      className="btn btn-success class d-none"
                      onClick={(e) => handleClickSave(e)}
                    >
                      save
                    </button>
                    <button
                      id="cancel"
                      type="button"
                      className="btn btn-danger class d-none"
                    >
                      Cancel
                    </button>
                  </div>
                )}
              </div>

              {visible && (
                <textarea
                  id="textarea"
                  className="form-control class d-none"
                ></textarea>
              )}
            </form>
          </div>
        </div>
      </div>
    </Container>
  );
};

Task.getInitialProps = async (ctx) => {
  const res = await fetch(
    `https://jsonplaceholder.typicode.com/todos/${ctx.query.id}`
  );
  const data = await res.json();
  return { _task: data };
};

export default Task;
