import Router from "next/router";
import { deleteTask, putTodo } from "../store/actions/todoAction";
import { useDispatch } from "react-redux";
import { useState, useEffect } from "react";
const Tasks = ({ tasks }) => {
  const [taskList, setTaskList] = useState([]);
  const [check, setCheck] = useState();
  const dispatch = useDispatch();

  useEffect(() => {
    console.log("r");
    if (tasks.length) {
      const order = tasks.sort((a, b) => {
        return Number(a.completed) - Number(b.completed);
      });
      setTaskList(order);
    }
  }, [tasks]);

  const handleClick = (event, id) => {
    event.preventDefault();
    if (document.getElementById(id)) {
      dispatch(deleteTask(id));
    }
  };

  const handleChange = (event) => {
    event.preventDefault();
    const newTasks = tasks.filter(({ title }) => {
      return title.includes(event.target.value);
    });
    setTaskList(newTasks);
  };

  const handleCheck = (event, task) => {
    const newobject = {
      userId: task.userId,
      id: task.id,
      title: task.title,
      completed: event.target.checked,
    };
    dispatch(putTodo(newobject));
  };

  return (
    <>
      <div className="row m-4 sticky-top bg-success d-flex">
        <label className="col-8">
          Search:
          <input
            placeholder="Search a task"
            className="form-control col-6"
            onChange={(e) => handleChange(e)}
          ></input>
        </label>
        <div  className="col-4">
          <select>
            <option value="true">Task Complete</option>
            <option value="false">Task Incomplete</option>
          </select>
        </div>
      </div>
      <ul>
        {taskList.map((task, i) => (
          <li key={i} id={task.id}>
            <button onClick={(e) => handleClick(e, task.id)}>
              <i className="fas fa-trash-alt"></i>
            </button>
            <label
              name={task.id}
              onClick={(e) => {
                e.preventDefault();
                Router.push("/tasks/[id]", `/tasks/${task.id}`);
              }}
            >
              {task.title}
            </label>
            <input
              type="checkbox"
              checked={task.completed}
              onChange={(e) => handleCheck(e, task)}
            />
          </li>
        ))}
      </ul>
    </>
  );
};

export default Tasks;
