import Router from "next/router";
const Tasks = ({ tasks }) => {
  return (
    <>
      <ul>
        {tasks.map((task) => (
          <li key={task.id}>
            <button><i className="fas fa-trash-alt"></i></button>
            <p
              onClick={(e) => {
                e.preventDefault();
                Router.push("/tasks/[id]", `/tasks/${task.id}`);
              }}
            >
              {task.title}
            </p>
          </li>
        ))}
      </ul>
    </>
  );
};


export default Tasks;
