import { useRouter } from "next/router";
import Container from "../../components/Container";
import { useState, useEffect } from "react";
import { putTodo, getTask } from "../../store/actions/todoAction";
import { useDispatch } from "react-redux";

const Task = () => {
  const router = useRouter();
  const { id } = router.query;
  console.log(id)

  getTask(id).then(res => console.log(res))

  console.log(getData(id))

  const [visible, setVisible] = useState(false);
  const [update, setUpdate] = useState(true);

  // const handleSubmit = (event) => {
  //   event.preventDefault();
  //   const newTask = event.target.textarea.value;
  //   console.log(newTask);
  //   if (newTask && newTask.length != 0) {
  //     const newobject = {
  //       userId: _task.userId,
  //       id: _task.id,
  //       title: newTask,
  //       completed: _task.completed,
  //     };
  //     dispatch(putTodo(newobject));
  //   }
  // };
  const handleClickUpdate = (event) => {
    event.preventDefault();
    setVisible(true);
    setUpdate(false);
  };
  const handleClickCancel = (event) => {
    event.preventDefault();
    setVisible(false);
    setUpdate(true);
  };

  return (
    <Container>
      <div className="taskCard">
        <div className="card">
          <div className="card-body">
            <h5 className="card-title">Task: #</h5>
            <p className="card-text">
              Description: <br />
               
            </p>
          </div>
          <div className="card-footer">
            <form onSubmit={(e) => handleSubmit(e)}>
              <div className="d-flex mb-2">
                {update && (
                  <button
                    id="update"
                    type="button"
                    className="btn btn-info"
                    onClick={(e) => handleClickUpdate(e)}
                  >
                    Update
                  </button>
                )}
                {visible && (
                  <div>
                    <button
                      id="save"
                      type="submit"
                      className="btn btn-success mr-2"
                    >
                      Save
                    </button>
                    <button
                      id="cancel"
                      type="button"
                      className="btn btn-danger"
                      onClick={(e) => handleClickCancel(e)}
                    >
                      Cancel
                    </button>
                  </div>
                )}
              </div>

              {visible && (
                <textarea id="textarea" className="form-control"></textarea>
              )}
            </form>
          </div>
        </div>
      </div>
    </Container>
  );
};

// // Task.getInitialProps = (ctx) => {
// //   const res =  getTask("ctx.query.id")

// //   const data =  res.json();
// //   return { _task: data };
// // };

export default Task;
