import Router from "next/router";
const Tasks = ({ tasks }) => {

  const handleSubmit = (event) =>{
    event.preventDefault();
    const task = event.target.task.value
    if (task && task.length !=0) {
      const object = {
        userId: 1,
        title: task,
        completed: false
      }
      dispatch(postTodo(object))
      alert("Task created")      
    }

  }

  return (
    <>
      <ul>
        {tasks.map((task,i) => (
          <li key={i} onSubmit={(e) => handleSubmit(e)}>
            <button type="submit"><i className="fas fa-trash-alt"></i></button>
            <label
              name={task.id}
              onClick={(e) => {
                e.preventDefault();
                Router.push("/tasks/[id]", `/tasks/${task.id}`);
              }}
            >
              {task.title}
            </label>
          </li>
        ))}
      </ul>
    </>
  );
};


export default Tasks;
