import Router from "next/router";
const Tasks = ({ tasks }) => {

  

  return (
    <>
      <ul>
        {tasks.map((task,i) => (
          <li key={i}>
            <button type="submit"><i className="fas fa-trash-alt"></i></button>
            <label
              htmlFor={task.id}
              onClick={(e) => {
                e.preventDefault();
                Router.push("/tasks/[id]", `/tasks/${task.id}`);
              }}
            >
              {task.title}
            </label>
          </li>
        ))}
      </ul>
    </>
  );
};


export default Tasks;
