import { useRouter } from "next/router";
import Container from "../../components/Container";

const Task = ({ _task }) => {
  const router = useRouter();
  const { id } = router.query;

  const handleClickUpdate = (event) => {
    event.preventDefault();
      const save = document.getElementById("save");
      const cancel = document.getElementById("cancel");
      const textarea = document.getElementById("textarea");
      cancel.setAttribute("class","d-none");
      save.setAttribute("class","d-none");
      textarea.setAttribute("class","d-none");
    // const task = event.target.task.value;
    // if (task && task.length != 0) {
    //   const object = {
    //     userId: 1,
    //     title: task,
    //     completed: false,
    //   };
    //   dispatch(postTodo(object));
    // }
  };

  return (
    <Container>
      <div className="taskCard">
        <div className="card">
          <div className="card-body">
            <h5 className="card-title">Task: #{_task.id}</h5>
            <p className="card-text">
              Description: <br />
              {_task.title}
            </p>
          </div>
          <div className="card-footer">
            <form>
              <div className="d-flex mb-2">
                <button name="update" type="button" className="btn btn-info" onClick={e => handleClickUpdate(e)}>
                  Update
                </button>
                <button name="save" type="submit" className="btn btn-success class">
                  save
                </button>
                <button name="cancel" type="button" className="btn btn-danger class">
                  Cancel
                </button>
              </div>
              <textarea
                name="textarea"
                className="form-control class"
              ></textarea>
            </form>
          </div>
        </div>
      </div>
    </Container>
  );
};

Task.getInitialProps = async (ctx) => {
  const res = await fetch(
    `https://jsonplaceholder.typicode.com/todos/${ctx.query.id}`
  );
  const data = await res.json();
  return { _task: data };
};

export default Task;
